from string import ascii_letters
from secret import text

heiroglyphs = [chr(i) for i in range(0x13000, 0x13000 + len(ascii_letters))]
substition = {letter : glyph for letter, glyph in zip(ascii_letters, heiroglyphs)}

cipher = ""
for letter in text:
    if letter in substition:
        cipher += substition[letter]
    else:
        cipher += letter
    
print(cipher)

